SELECT count(1) FROM appInfos app, devUser dev, devUser devtwo, `STATUS` st, frame fr, flatform ff, firstappclass fa, twoAppClass ta, threeAPPClass tha, appVersion av WHERE app.devUserId = dev.id AND app.statusId = st.id AND app.frameId = fr.id AND app.flatformId = ff.id AND app.createdBy = devtwo.id AND app.firstAPPClassId = fa.id AND app.twoAPPClassId = ta.id AND app.threeAPPClassId = tha.id AND app.versionId = av.id
AND app.id = #{appId,jdbcType=INTEGER}
AND app.softwareName like CONCAT('%',#{softwareName},'%')
AND app.flatformId = #{flatformId}
AND app.firstAPPClassId = #{firstAPPClassId}
AND app.twoAPPClassId = #{twoAPPClassId}
AND app.threeAPPClassId = #{threeAPPClassId}
AND app.devUserId = #{devUserId}
AND app.frameId = #{frameId}
AND app.statusId = #{statusId}

SELECT app.id "appId", app.softwareName "softwareName", app.APKName "APKName", app.supportROM "supportROM", app.interfaceLanguageId "interfaceLanguageId", app.softwareSize "softwareSize", app.updateDate "updateDate", app.devUserId "devUserId", app.appIntroduction "appIntroduction", app.statusId "statusId", app.frameId "frameId", app.onSaleDate "onSaleDate", app.offSaleDate "offSaleDate", app.flatformId "flatformId", app.downloads "downloads", app.createdBy "createdBy", app.creationDate "creationDate", app.firstAPPClassId "firstAPPClassId", app.twoAPPClassId "twoAPPClassId", app.threeAPPClassId "threeAPPClassId", app.logoPicPath "logoPicPath", app.logoLocPath "logoLocPath", app.versionId "versionId", dev.devName "devUserName", devtwo.devName "createByName",
st.statusType "statusType", fr.frameType "frameType", ff.flatformName "flatformName", fa.firstAPPName "firstAPPName", ta.twoAPPName "twoAPPName", tha.threeAPPName "threeAPPName", av.versionNo "versionNo" FROM appInfos app, devUser dev, devUser devtwo, `STATUS` st, frame fr, flatform ff, firstappclass fa, twoAppClass ta, threeAPPClass tha, appVersion av WHERE app.devUserId = dev.id AND app.statusId = st.id AND app.frameId = fr.id AND app.flatformId = ff.id AND app.createdBy = devtwo.id AND app.firstAPPClassId = fa.id AND app.twoAPPClassId = ta.id AND app.threeAPPClassId = tha.id AND app.versionId = av.id
AND app.id = #{appId,jdbcType=INTEGER}
AND app.softwareName like CONCAT('%',#{softwareName},'%')
AND app.flatformId = #{flatformId}
AND app.firstAPPClassId = #{firstAPPClassId}
AND app.twoAPPClassId = #{twoAPPClassId}
AND app.threeAPPClassId = #{threeAPPClassId}
AND app.devUserId = #{devUserId}
AND app.frameId = #{frameId}
AND app.statusId = #{statusId}
LIMIT #{currNo},#{size}
